import { Link } from 'react-router-dom'
import React, { useContext, useState  } from 'react'

import styles from './Menu.module.css'

import Logo from '../../assets/img/logo.png'

//import { ReactDimmer } from 'react-dimmer'

//import { Container } from './styles'

/*icons*/
//import { FaBars } from 'react-icons/fa'
//import { FiLogOut } from 'react-icons/fi'

/* contexts */
import { Context } from '../../context/UserContext';

/* hooks */


//import React, { useState } from "react";
import styled from "styled-components";
//import { Link } from "react-router-dom";
import * as FaIcons from "react-icons/fa";
import * as AiIcons from "react-icons/ai";
//import { SidebarData } from "../SidebarData";
//import { SidebarDataAutenticado } from '../SidebarDataAutenticado';
//import { SubMenu } from "../SubMenu";
import { IconContext } from "react-icons/lib";
//import SidebarData from "../SidebarData";

//import  SidebarDataAutenticado from '../SidebarDataAutenticado';



//Menu fixo da NAVBAR  
const Nav = styled.div`
  max-width: 2600px;
  width: 100%;
  background: rgb(41, 41, 241);
  position: fixed;
  top: 0;
  padding: 0 20px;
  height: 80px;
  display: flex;
  /*justify-content: flex-start;*/
  align-items: center;
  justify-content: space-between;    
  /*z-index: 999;*/
  
  img{
    margin-top: 5px;
    /*align-items: center;*/
    height: 60px;
    width: 100px;
    margin-right: .8em;
    border-radius: 5px;

  }

  h2{
    color: #FFF;
    margin-left: -1510px;
  }


ul {
  display: flex;
  align-items: center;
  list-style: none;
  }


li, a {
  /*display: block;*/
  font-size: 18px;
  text-decoration: none;
  list-style: none;
  color: #FFF;
  font-weight: bold;
  margin-left: 10px;
  align-items: center;
  justify-content: space-between;  
  cursor: pointer;
  transition: .5s;
  padding: .4em .6em;
  border-radius: 5px;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  -ms-border-radius: 5px;
  -o-border-radius: 5px;
  
}


li:hover{    
    margin-left: 5px;
    background-color: rgb(224, 10, 10);
    color: #FFF;
  }

li > a:hover{    
    margin-left: 5px;
    color: #FFF;
  }

  @media screen and (max-width: 850px){

  ul{
      display: none;
    }

  img{
    margin-left: 0;
  }

  }  
  
`;
  

const NavIcon = styled(Link)` //É NESSE AQUI PRA MEXER
  display: none;
  margin-left: 2rem;  
  font-size: 3rem;
  height: 80px;  
  justify-content: flex-start;  
  align-items: center;
  
  
  
  @media screen and (max-width: 850px){    
  display: block;
  margin-top: 30px;
  margin-left: 2rem;
  font-size: 2rem;
  text-decoration: none;
  
  height: 80px;   
  justify-content: flex-start;  
  align-items: center;
  z-index: 999;
  /*padding-right: -50px;*/

  }
  
`;
  

//Tela quando clica no MENU
const SidebarNav = styled.nav`  
  background: rgb(41, 41, 241);  
  width: 280px;
  height: 100vh;
  display: flex;
  justify-content: center;
  position: fixed;  
  top: 0;
  left: ${({ sidebar }) => (sidebar ? "0" : "-100%")};
  transition: 350ms;
  z-index: 10;

  
  @media screen and (max-width: 850px){

    li, a {
  /*display: block;*/
  font-size: 18px;
  text-decoration: none;
  list-style: none;
  color: #FFF;
  font-weight: bold;
  margin-left: 10px;
  align-items: center;
  justify-content: space-between;  
  cursor: pointer;
  transition: .5s;
  padding: .4em .6em;
  border-radius: 5px;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  -ms-border-radius: 5px;
  -o-border-radius: 5px;
  
}

  li:hover{    
    margin-left: 15px;
    margin-right: 15px;
    background-color: rgb(224, 10, 10);
    color: #FFF;
    border-left: 6px solid #FFF;
  }

li > a:hover{    
    margin-left: 5px;
    color: #FFF;
  }


  /*display: block;
  margin-top: 30px;
  margin-left: 2rem;
  font-size: 2rem;
  height: 80px;   
  justify-content: flex-start;  
  align-items: center;
  z-index: 999;
  padding-right: -50px;*/

  }
  
`;  

  
const SidebarWrap = styled.div`
  width: 100%;
`;



//function Menu()
const Menu = () => {

  const [sidebar, setSidebar] = useState(false);
  
  const showSidebar = () => setSidebar(!sidebar);


  const { authenticated, logout } = useContext(Context);
  

   return (    
       <>    
       <IconContext.Provider value={{ color: "#fff" }}>        
        <Nav>
          <NavIcon to="#">
            <FaIcons.FaBars onClick={showSidebar} />
          </NavIcon>
            <div className={styles.navbar_logo}>
              <Link to="/"><img src={Logo} alt="SRM - Sistema de Reserva Multimídia"/></Link>
            </div>
      <ul>
        <li>
          <Link to="/">Adotar</Link>
        </li>
        {authenticated ? (
          <>          
            <li>
              <Link to="/pet/myadoptions">Minhas Adoções</Link>
            </li>
            <li>
              <Link to="/pet/mypets">Meus Pets</Link>
            </li>
            <li>
              <Link to="/user/profile">Meu Perfil</Link>
            </li>
            <li onClick={logout}>Sair</li>
          </>
        ) : (
          <>
            <li>
            <Link to="/login">Entrar</Link>
            </li>
            
          </>
        )}
      </ul >                                
        </Nav>          
          <SidebarNav sidebar={sidebar}>            
          <SidebarWrap>
            <NavIcon to="#">
              <AiIcons.AiOutlineClose onClick={showSidebar} />
            </NavIcon>
            <ul>
        <li>
          <Link to="/">Adotar</Link>
        </li>
        {authenticated ? (
          <>          
            <li>
              <Link to="/pet/myadoptions">Minhas Adoções</Link>
            </li>
            <li>
              <Link to="/pet/mypets">Meus Pets</Link>
            </li>
            <li>
              <Link to="/user/profile">Meu Perfil</Link>
            </li>
            <li>
              <Link onClick={logout}>Sair</Link>
            </li>
                                   
          </>
        ) : (
          <>
            <li>
            <Link to="/login">Entrar</Link>
            </li>
            
          </>
        )}
      </ul >
          </SidebarWrap>
        </SidebarNav>
      </IconContext.Provider>    
    </>     
  );
  
};


export default Menu;
